#include <reg51.h>
#include <stdio.h>

unsigned char coded[8] = {0xfe,0xfd,0xfb,0xf7,0xef,0xdf,0xbf,0x7f};
void fun1();
void fun2();
void fun3();
sbit P20 = P2^0;
void delay(){
	int i=10000;
	while(i--);
	}
